import axios from 'axios'
const API_URL = 'http://localhost:8080/api/course'

class CourseService {
  
  post(title, description, price) {
    let token;
    if (JSON.parse(localStorage.getItem('user'))) {
      token = JSON.parse(localStorage.getItem('user')).token
    } else {
      token = ''
    }

    axios.post(API_URL, {title, description, price}, {
      headers: {
        Authorization: token
      }
    })
  }

  get(_id) {
    let token;
    if (JSON.parse(localStorage.getItem('user'))) {
      token = JSON.parse(localStorage.getItem('user')).token
    } else {
      token = ''
    }

    console.log(token)

    axios.get(API_URL+'/instructor/'+_id, {
      headers: {
        Authorization: token
      }
    })
  }

  getEnrolledCourses(_id) {
    let token;
    if (JSON.parse(localStorage.getItem('user'))) {
      token = JSON.parse(localStorage.getItem('user')).token
    } else {
      token = ''
    }

    console.log(API_URL+'/student/'+_id)

    axios.get(API_URL+'/student/'+_id, {
      headers: {
        Authorization: token
      }
    })
  }


}

let courseService = new CourseService()

export default courseService
